function() {
	var started = $value("repeatActionInstance.record.started");
	var stopped = $value("repeatActionInstance.record.stopped");
	if (!stopped) {
		return false;
	}
	// only show if its not the same day as the start
	if (stopped.getFullYear() != started.getFullYear()
			|| stopped.getDate() != started.getDate()
			|| stopped.getMonth() != started.getMonth()) {
		return true;
	}
	return false;
}

duration.javascript:
function formatTime(value) {
    var differenceInMilliseconds = value.stopped.getTime() - value.started.getTime();

    var totalSeconds = differenceInMilliseconds / 1000;
    var hours = Math.floor(totalSeconds / 3600);
    var minutes = Math.floor((totalSeconds % 3600) / 60);
    var seconds = Math.floor(totalSeconds % 60);
    var milliseconds = differenceInMilliseconds % 1000;

    var formattedTimeParts = [];

    if (hours > 0) {
        formattedTimeParts.push(hours + "h");
    }
    if (minutes > 0 || hours > 0) {  // Also show minutes if hours are present, even if minutes are zero.
        formattedTimeParts.push(minutes + "m");
    }
    if (seconds > 0 || (minutes > 0 || hours > 0)) {
        formattedTimeParts.push(seconds + "s");
    }
    if (seconds === 0 && minutes === 0 && hours === 0) {
        formattedTimeParts.push(milliseconds + "ms");
    } else if (milliseconds > 0) {
        formattedTimeParts.push(milliseconds + "ms");
    }

    var formattedTime = formattedTimeParts.join(' ');

    return formattedTime;
}

